import React from 'react';
import CategoryGrid from './CategoryGrid';
import ItemGrid from './ItemGrid';
import OrderSidebar from './OrderSidebar';
import ActionBar from './ActionBar';
import styles from '../styles/MainPage.module.css';

export default function MainPage({ view, onSelectCategory, onAddItem, orderItems, onVoidLast, onClearAll, onPay }) {
  return (
    <div className={styles.container}>
      <div className={styles.mainArea}>
        {view.type === 'categories'
          ? <CategoryGrid onSelectCategory={onSelectCategory} />
          : <ItemGrid categoryId={view.categoryId} onAddItem={onAddItem} />
        }
        <OrderSidebar orderItems={orderItems} onVoidLast={onVoidLast} onClearAll={onClearAll} />
      </div>
      <ActionBar onVoidLast={onVoidLast} onClearAll={onClearAll} onPay={onPay} />
    </div>
  );
}
