import React, { useState } from 'react';
import MainPage from './components/MainPage';
import PaymentModal from './components/PaymentModal';
import Receipt from './components/Receipt';
import './styles/index.css';

function App() {
  const [view, setView] = useState({ type: 'categories' });
  const [orderItems, setOrderItems] = useState([]);
  const [showPayment, setShowPayment] = useState(false);
  const [showReceipt, setShowReceipt] = useState(false);

  const handleSelectCategory = categoryId => setView({ type: 'items', categoryId });
  const handleAddItem = (item, modifiers) => setOrderItems([...orderItems, { item, modifiers }]);
  const handleVoidLast = () => setOrderItems(orderItems.slice(0, -1));
  const handleClearAll = () => setOrderItems([]);
  const handlePay = () => setShowPayment(true);
  const handlePaymentComplete = () => {
    setShowPayment(false);
    setShowReceipt(true);
  };
  const handleFinish = () => {
    setShowReceipt(false);
    setOrderItems([]);
    setView({ type: 'categories' });
  };

  return (
    <>
      {!showPayment && !showReceipt && (
        <MainPage
          view={view}
          onSelectCategory={handleSelectCategory}
          onAddItem={handleAddItem}
          orderItems={orderItems}
          onVoidLast={handleVoidLast}
          onClearAll={handleClearAll}
          onPay={handlePay}
        />
      )}
      {showPayment && (
        <PaymentModal onCancel={() => setShowPayment(false)} onComplete={handlePaymentComplete} />
      )}
      {showReceipt && <Receipt orderItems={orderItems} onFinish={handleFinish} />}
    </>
  );
}

export default App;
